package coreJava;

public class NumberPyramidReverse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int k = 1;
		for(int i =1;i<=4;i++) {
			
			
			for(int j=0;j<i;j++) {
				
				System.out.print(k);
				System.out.print(" ");
//				System.out.print("\t");
				k++;
			}
			
			System.out.println("");

	}


	}

}
